import { ColorQuery, Image, MatchResult, Point, Region, TextQuery, WindowElementQuery, WindowQuery } from "@nut-tree-fork/shared/dist/lib/objects";
import { WindowInterface } from "@nut-tree-fork/shared/dist/lib/types/window.interface";
export type RegionResultFindInput = Image | TextQuery;
export type PointResultFindInput = ColorQuery;
export type WindowResultFindInput = WindowQuery;
export type WindowElementResultFindInput = WindowElementQuery;
