export type FindResult = Region | Point | WindowInterface;
export type WindowCallback = (target: WindowInterface) => void | Promise<void>;
export type MatchResultCallback<TARGET_TYPE> = (target: MatchResult<TARGET_TYPE>) => void | Promise<void>;
export type FindHookCallback = WindowCallback | MatchResultCallback<Point> | MatchResultCallback<Region>;
